#include "stdlib/stats/base/dists/arcsine/mode.h"
#include <stdlib.h>
#include <stdio.h>
#include <math.h>

static double random_uniform( const double min, const double max ) {
    double v = (double)rand() / ( (double)RAND_MAX + 1.0 );
    return min + ( v * ( max - min ) );
}

int main( void ) {
    double a;
    double b;
    double m;
    int i;

    printf( "Mode of the arcsine distribution:\n" );
    printf( "a\t\tb\t\tMode\n" );
    printf( "----------------------------------------\n" );

    for ( i = 0; i < 10; i++ ) {
        a = random_uniform( -20.0, 0.0 );
        b = random_uniform( a, a + 40.0 );
        m = stdlib_base_dists_arcsine_mode( a, b );

        if ( isnan( m ) ) {
            printf( "a: %lf, b: %lf, Mode: NaN (Invalid Input)\n", a, b );
        } else {
            printf( "a: %lf, b: %lf, Mode: %lf\n", a, b, m );
        }
    }

    return 0;
}
