#include <math.h>
#include <stdbool.h>
#include "stdlib/math/base/assert/is_nan.h"

/**
* Returns the mode of an arcsine distribution.
*
* @param a    minimum support
* @param b    maximum support
* @return     mode of the distribution, or NaN if inputs are invalid
*
* @example
* double v = mode( 0.0, 1.0 );
* // returns 0.0
*
* @example
* double v = mode( 4.0, 12.0 );
* // returns 4.0
*
* @example
* double v = mode( -4.0, 4.0 );
* // returns -4.0
*
* @example
* double v = mode( 1.0, -0.1 );
* // returns NaN
*
* @example
* double v = mode( 2.0, NAN );
* // returns NaN
*
* @example
* double v = mode( NAN, 2.0 );
* // returns NaN
*/
double stdlib_base_dists_arcsine_mode( const double a, const double b ) {
    if (
        stdlib_base_is_nan( a ) ||
        stdlib_base_is_nan( b ) ||
        a >= b
    ) {
        return NAN;
    }
    return a;
}
