'use strict';

// MODULES //

var constantFunction = require( '@stdlib/utils/constant-function' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );


// VARIABLES //

var SQRT1OVER8 = 0.35355339059327373; // sqrt(1.0 / 8.0)


// MAIN //

/**
* Returns a function for calculating the standard deviation of an arcsine distribution with minimum support `a` and maximum support `b`.
*
* @param {number} a - minimum support
* @param {number} b - maximum support
* @returns {Function} Standard deviation function
*
* @example
* var stdev = factory( 0.0, 10.0 );
* var y = stdev();
* // returns ~3.536
*/
function factory( a, b ) {
	if (
		isnan( a ) ||
		isnan( b ) ||
		a >= b
	) {
		return constantFunction( NaN );
	}
	return stdev;

	/**
	* Calculates the standard deviation of an arcsine distribution.
	*
	* @private
	* @returns {number} Standard deviation
	*
	* @example
	* var y = stdev();
	* // returns <number>
	*/
	function stdev() {
		return SQRT1OVER8 * ( b - a );
	}
}


// EXPORTS //

module.exports = factory;
