#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/stats/base/dists/chi/mean.h"

/**
* Returns the variance of a chi distribution.
*
* @param k    degrees of freedom (must be non-negative)
* @return     variance, or NaN if input is invalid
*
* @example
* double v = stdlib_base_dists_chi_variance( 9.0 );
* // returns ~0.485
*
* @example
* double v = stdlib_base_dists_chi_variance( 1.0 );
* // returns ~0.363
*
* @example
* double v = stdlib_base_dists_chi_variance( -0.2 );
* // returns NaN
*
* @example
* double v = stdlib_base_dists_chi_variance( NAN );
* // returns NaN
*/
double stdlib_base_dists_chi_variance( const double k ) {
    if ( stdlib_base_is_nan( k ) || k < 0.0 ) {
        return 0.0 / 0.0; // NaN
    }
    double mu = stdlib_base_dists_chi_mean( k );
    return k - ( mu * mu );
}
