#include "stdlib/math/base/assert/is_nonnegative_integer.h"
#include "stdlib/constants/float64/pinf.h"
#include "stdlib/math/base/special/sqrt.h"

/**
* Returns the standard deviation of a hypergeometric distribution.
*
* @param N    population size
* @param K    subpopulation size
* @param n    number of draws
* @return     standard deviation of the distribution, or NaN if inputs are invalid
*
* @example
* double sd = stdlib_base_dists_hypergeometric_stdev( 16, 11, 4 );
* // returns ~0.829
*
* @example
* double sd = stdlib_base_dists_hypergeometric_stdev( 2, 1, 1 );
* // returns 0.5
*
* @example
* double sd = stdlib_base_dists_hypergeometric_stdev( 10, 5, 12 );
* // returns NaN
*/
double stdlib_base_dists_hypergeometric_stdev( const double N, const double K, const double n ) {
    if (
        !stdlib_base_is_nonnegative_integer( N ) ||
        !stdlib_base_is_nonnegative_integer( K ) ||
        !stdlib_base_is_nonnegative_integer( n ) ||
        N == STDLIB_CONSTANT_FLOAT64_PINF ||
        K == STDLIB_CONSTANT_FLOAT64_PINF ||
        K > N ||
        n > N
    ) {
        return NAN;
    }

    double variance = n * (K / N) * ((N - K) / N) * ((N - n) / (N - 1));
    return stdlib_base_sqrt( variance );
}
